// =============================================================================
// HELPER FUNCTIONS
// =============================================================================

/**
 * Get all guides with their slugs
 */
export function getAllGuides(): Array<{ slug: GuideSlug; data: GuidePostContent }> {
  return guideSlugs
    .filter((slug) => guideContent[slug])
    .map((slug) => ({
      slug,
      data: guideContent[slug]!,
    }))
}

/**
 * Get featured guides
 */
export function getFeaturedGuides(): Array<{ slug: GuideSlug; data: GuidePostContent }> {
  return getAllGuides().filter(({ data }) => data.featured)
}

/**
 * Get a single guide by slug
 */
export function getGuide(slug: string): GuidePostContent | undefined {
  return guideContent[slug as GuideSlug]
}

/**
 * Get guides by cluster
 */
export function getGuidesByCluster(cluster: ClusterType): Array<{ slug: GuideSlug; data: GuidePostContent }> {
  return getAllGuides().filter(({ data }) => data.cluster === cluster)
}

